,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'process_qutation'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'DECLARE',
'  CURSOR C IS SELECT INVOICE_NO FROM INVOICE WHERE LTRIM(RTRIM(QUOTE_NO))=LTRIM(RTRIM(:P19_QUOTE_NO));',
'  R C%ROWTYPE;',
'  VINVOICE_NO VARCHAR2(20);',
'  VTOTAL NUMBER(11,2):=0;',
'  VVAT_PERC   NUMBER(11,2):=10;',
'  VVAT        NUMBER(11,2):=0;',
'BEGIN',
'    OPEN C; FETCH C INTO R; CLOSE C;',
'    IF R.INVOICE_NO IS NULL THEN',
'        SELECT ''INV''||TO_CHAR(SYSDATE,''RRRR'')||LTRIM(RTRIM(TO_CHAR(NVL(MAX(SUBSTR(LTRIM(RTRIM(INVOICE_NO)),8)),10000)+1))) ',
'        INTO   :P19_INVOICE_NO',
'        FROM INVOICE WHERE SUBSTR(LTRIM(RTRIM(INVOICE_NO)),4,4)=TO_CHAR(SYSDATE,''RRRR'');',
'    END IF;',
'    UPDATE INVOICE SET',
'    INVOICE_NO    = :P19_INVOICE_NO,',
'    INVOICE_DATE  = :P19_INVOICE_DATE,',
'    QUOTE_DATE    = :P19_QUOTE_DATE,',
'    JOB_NO        = :P19_JOB_NO,',
'    CAR           = :P19_CAR,',
'    TOTAL         = :P19_TOTAL,',
'    VAT_PERC      =10,',
'    VAT           =NVL(:P19_TOTAL,0)*10/100,',
'    NET_AMOUNT    =NVL(:P19_TOTAL,0) + NVL(:P19_TOTAL,0)*10/100,',
'    STATUS        =''Q'',',
'    DISCOUNT      =:P19_DISCOUNT,',
'    KM            =:P19_KM',
'    WHERE LTRIM(RTRIM(QUOTE_NO))=LTRIM(RTRIM(:P19_QUOTE_NO));',
'    IF SQL%NOTFOUND THEN',
'      SELECT ''QTN''||TO_CHAR(SYSDATE,''RRRR'')||LTRIM(RTRIM(TO_CHAR(NVL(MAX(SUBSTR(LTRIM(RTRIM(QUOTE_NO)),8)),10000)+1))) ',
'      INTO   :P19_QUOTE_NO',
'      FROM INVOICE WHERE SUBSTR(LTRIM(RTRIM(QUOTE_NO)),4,4)=TO_CHAR(SYSDATE,''RRRR'');',
'',
'      SELECT ''INV''||TO_CHAR(SYSDATE,''RRRR'')||LTRIM(RTRIM(TO_CHAR(NVL(MAX(SUBSTR(LTRIM(RTRIM(INVOICE_NO)),8)),10000)+1))) ',
'      INTO   :P19_INVOICE_NO',
'      FROM INVOICE WHERE SUBSTR(LTRIM(RTRIM(INVOICE_NO)),4,4)=TO_CHAR(SYSDATE,''RRRR'');',
'',
'      INSERT INTO INVOICE  (QUOTE_NO,INVOICE_NO,INVOICE_DATE,JOB_NO,QUOTE_DATE,',
'                            CAR,TOTAL,VAT_PERC,VAT,NET_AMOUNT,',
'                            STATUS,DISCOUNT,KM)',
'      VALUES               (:P19_QUOTE_NO,:P19_INVOICE_NO,SYSDATE,:P19_JOB_NO,SYSDATE,',
'                            :P19_CAR,:P19_TOTAL,:P19_VAT_PERC,:P19_VAT,:P19_NET_AMOUNT,',
'                            :P19_STATUS,:P19_DISCOUNT,:P19_KM);',
'    END IF;',
'    apex_application.g_print_success_message := ''<span>''||''Quotation No ''||:P19_QUOTE_NO||'' Saved.''||''</span>'';',
'END;'))
,p_process_clob_language=>'PLSQL'
,p_error_display_location=>'INLINE_IN_NOTIFICATION'
,p_process_when_button_id=>wwv_flow_imp.id(45511447724219963526)
);
